}

contracts/AuthenticityNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "erc721a/contracts/ERC721A.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Base64.sol";

/**
 * @title AuthenticityNFT
 * @dev ERC721A NFT contract for authenticity certificates
 * @notice This contract creates NFT certificates for verified authentic content
 */
contract AuthenticityNFT is ERC721A, Ownable, ReentrancyGuard {
    using Strings for uint256;

    // Struct to store NFT metadata
    struct AuthenticityMetadata {
        string ipfsHash;
        uint256 confidenceScore;
        string aiModel;
        uint256 verificationTimestamp;
        address originalRequester;
        bool isRevoked;
    }

    // Contract state
    string private _baseTokenURI;
    mapping(uint256 => AuthenticityMetadata) public tokenMetadata;
    mapping(string => uint256) public ipfsToTokenId; // ipfsHash => tokenId
    mapping(address => bool) public authorizedMinters;

    // Statistics
    uint256 public totalMinted;
    uint256 public totalRevoked;

    // Events
    event AuthenticityNFTMinted(
        address indexed to,
        uint256 indexed tokenId,
        string ipfsHash,
        uint256 confidenceScore,
        string aiModel,
        uint256 timestamp
    );

    event NFTRevoked(
        uint256 indexed tokenId,
        string reason,
        uint256 timestamp
    );

    event BaseURIUpdated(
        string oldBaseURI,
        string newBaseURI,
        uint256 timestamp
    );

    event MinterAuthorized(
        address indexed minter,
        bool authorized,
        uint256 timestamp
    );

    /**
     * @dev Constructor
     * @param _name Token name
     * @param _symbol Token symbol
     * @param _baseURI Base URI for token metadata
     * @param _initialOwner Initial owner of the contract
     */
    constructor(
        string memory _name,
        string memory _symbol,
        string memory _baseURI,
        address _initialOwner
    ) ERC721A(_name, _symbol) Ownable(_initialOwner) {
        _baseTokenURI = _baseURI;
    }

    /**
     * @notice Mint authenticity NFT (only authorized minters)
     * @param _to Address to mint NFT to
     * @param _ipfsHash IPFS hash of verified content
     * @param _confidenceScore AI confidence score (0-100)
     * @param _aiModel AI model used for verification
     * @return tokenId The ID of the minted token
     */
    function mintAuthenticityNFT(
        address _to,
        string memory _ipfsHash,
        uint256 _confidenceScore,
        string memory _aiModel
    ) external nonReentrant returns (uint256) {
        require(authorizedMinters[msg.sender], "Not authorized to mint");
        require(_to != address(0), "Cannot mint to zero address");
        require(bytes(_ipfsHash).length > 0, "Invalid IPFS hash");
        require(_confidenceScore >= 50 && _confidenceScore <= 100, "Invalid confidence score");
        require(ipfsToTokenId[_ipfsHash] == 0, "NFT already exists for this content");

        uint256 tokenId = _nextTokenId();

        // Store metadata
        tokenMetadata[tokenId] = AuthenticityMetadata({
            ipfsHash: _ipfsHash,
            confidenceScore: _confidenceScore,
            aiModel: _aiModel,
            verificationTimestamp: block.timestamp,
            originalRequester: _to,
            isRevoked: false
        });

        // Update mappings
        ipfsToTokenId[_ipfsHash] = tokenId;
        totalMinted++;

        // Mint token
        _mint(_to, 1);

        emit AuthenticityNFTMinted(
            _to,
            tokenId,
            _ipfsHash,
            _confidenceScore,
            _aiModel,
            block.timestamp
        );

        return tokenId;
    }

    /**
     * @notice Revoke NFT (owner only - for emergency cases)
     * @param _tokenId Token ID to revoke
     * @param _reason Reason for revocation
     */
    function revokeNFT(
        uint256 _tokenId,
        string memory _reason
    ) external onlyOwner {
        require(_exists(_tokenId), "Token does not exist");
        require(!tokenMetadata[_tokenId].isRevoked, "Token already revoked");

        tokenMetadata[_tokenId].isRevoked = true;
        totalRevoked++;

        emit NFTRevoked(_tokenId, _reason, block.timestamp);
    }

    /**
     * @notice Get NFT metadata for a token
     * @param _tokenId Token ID
     * @return metadata The token metadata
     */
    function getTokenMetadata(
        uint256 _tokenId
    ) external view returns (AuthenticityMetadata memory) {
        require(_exists(_tokenId), "Token does not exist");
        return tokenMetadata[_tokenId];
    }

    /**
     * @notice Check if content has an NFT
     * @param _ipfsHash IPFS hash of content
     * @return tokenId Token ID (0 if no NFT exists)
     */
    function getTokenIdByIPFS(
        string memory _ipfsHash
    ) external view returns (uint256) {
        return ipfsToTokenId[_ipfsHash];
    }

    /**
     * @notice Get tokens owned by an address
     * @param _owner Owner address
     * @return tokenIds Array of token IDs
     */
    function getTokensByOwner(
        address _owner
    ) external view returns (uint256[] memory) {
        require(_owner != address(0), "Invalid owner address");

        uint256 balance = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](balance);
        uint256 currentIndex = 0;

        uint256 totalSupply = _nextTokenId() - _startTokenId();

        for (uint256 i = _startTokenId(); i < _startTokenId() + totalSupply && currentIndex < balance; i++) {
            if (_exists(i) && ownerOf(i) == _owner) {
                tokenIds[currentIndex] = i;
                currentIndex++;
            }
        }

        return tokenIds;
    }

    /**
     * @notice Generate token URI with dynamic metadata
     * @param _tokenId Token ID
     * @return Token URI string
     */
    function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {
        require(_exists(_tokenId), "Token does not exist");

        AuthenticityMetadata memory metadata = tokenMetadata[_tokenId];

        if (metadata.isRevoked) {
            return _buildRevokedTokenURI(_tokenId);
        }

        return _buildTokenURI(_tokenId, metadata);
    }

    /**
     * @notice Build token URI for active tokens
     * @param _tokenId Token ID
     * @param _metadata Token metadata
     * @return URI string
     */
    function _buildTokenURI(
        uint256 _tokenId,
        AuthenticityMetadata memory _metadata
    ) internal view returns (string memory) {
        string memory json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        '{"name": "Authenticity Certificate #',
                        _tokenId.toString(),
                        '", "description": "This NFT certifies the authenticity of digital content verified by AI analysis.", ',
                        '"image": "',
                        _baseTokenURI,
                        'certificate.png", ',
                        '"external_url": "https://gateway.pinata.cloud/ipfs/',
                        _metadata.ipfsHash,
                        '", ',
                        '"attributes": [',
                        '{"trait_type": "Confidence Score", "value": ',
                        _metadata.confidenceScore.toString(),
                        '}, ',
                        '{"trait_type": "AI Model", "value": "',
                        _metadata.aiModel,
                        '"}, ',
                        '{"trait_type": "Verification Date", "value": ',
                        _metadata.verificationTimestamp.toString(),
                        '}, ',
                        '{"trait_type": "Status", "value": "Verified Authentic"}, ',
                        '{"trait_type": "IPFS Hash", "value": "',
                        _metadata.ipfsHash,
                        '"}',
                        ']}'
                    )
                )
            )
        );

        return string(abi.encodePacked("data:application/json;base64,", json));
    }

    /**
     * @notice Build token URI for revoked tokens
     * @param _tokenId Token ID
     * @return URI string
     */
    function _buildRevokedTokenURI(uint256 _tokenId) internal view returns (string memory) {
        string memory json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        '{"name": "Revoked Certificate #',
                        _tokenId.toString(),
                        '", "description": "This authenticity certificate has been revoked.", ',
                        '"image": "',
                        _baseTokenURI,
                        'revoked.png", ',
                        '"attributes": [',
                        '{"trait_type": "Status", "value": "Revoked"}',
                        ']}'
                    )
                )
            )
        );

        return string(abi.encodePacked("data:application/json;base64,", json));
    }

    /**
     * @notice Set base URI (owner only)
     * @param _baseURI New base URI
     */
    function setBaseURI(string memory _baseURI) external onlyOwner {
        string memory oldBaseURI = _baseTokenURI;
        _baseTokenURI = _baseURI;

        emit BaseURIUpdated(oldBaseURI, _baseURI, block.timestamp);
    }

    /**
     * @notice Authorize/deauthorize minter (owner only)
     * @param _minter Minter address
     * @param _authorized Authorization status
     */
    function setAuthorizedMinter(
        address _minter,
        bool _authorized
    ) external onlyOwner {
        require(_minter != address(0), "Invalid minter address");
        authorizedMinters[_minter] = _authorized;

        emit MinterAuthorized(_minter, _authorized, block.timestamp);
    }

    /**
     * @notice Get contract statistics
     * @return totalMinted_ Total NFTs minted
     * @return totalRevoked_ Total NFTs revoked
     * @return totalActive Total active NFTs
     */
    function getStatistics() external view returns (
        uint256 totalMinted_,
        uint256 totalRevoked_,
        uint256 totalActive
    ) {
        return (totalMinted, totalRevoked, totalMinted - totalRevoked);
    }

    /**
     * @notice Check if NFT is valid (not revoked)
     * @param _tokenId Token ID
     * @return isValid Whether the NFT is valid
     */
    function isValidNFT(uint256 _tokenId) external view returns (bool) {
        if (!_exists(_tokenId)) {
            return false;
        }
        return !tokenMetadata[_tokenId].isRevoked;
    }

    /**
     * @notice Override _startTokenId to start from 1
     */
    function _startTokenId() internal pure override returns (uint256) {
        return 1;
    }

    /**
     * @notice Override transfer functions to prevent transfers of revoked tokens
     */
    function _beforeTokenTransfers(
        address from,
        address to,
        uint256 startTokenId,
        uint256 quantity
    ) internal view override {
        // Allow minting (from == address(0))
        if (from == address(0)) {
            return;
        }

        // Check if any token in the range is revoked
        for (uint256 i = 0; i < quantity; i++) {
            uint256 tokenId = startTokenId + i;
            require(!tokenMetadata[tokenId].isRevoked, "Cannot transfer revoked NFT");
        }
    }

    /**
     * @notice Get base URI
     * @return Base URI string
     */
    function baseURI() external view returns (string memory) {
        return _baseTokenURI;
    }

    /**
     * @notice Check if contract supports interface
     * @param interfaceId Interface ID
     * @return Whether interface is supported
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @notice Get contract version
     * @return Version string
     */
    function version() external pure returns (string memory) {
        return "1.0.0";
    }
}

contracts/AuthenticityRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@chainlink/contracts/src/v0.8/functions/v1_0_0/FunctionsClient.sol";
import "@chainlink/contracts/src/v0.8/functions/v1_0_0/libraries/FunctionsRequest.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./AuthenticityNFT.sol";

/**
 * @title AuthenticityRegistry
 * @dev Smart contract for verifying media authenticity using Chainlink Functions
 * @notice This contract integrates with AI services via Chainlink Functions to detect deepfakes
 */
contract AuthenticityRegistry is FunctionsClient, Ownable, ReentrancyGuard, Pausable {
    using FunctionsRequest for FunctionsRequest.Request;

    // Struct to store verification request data
    struct VerificationRequest {
        address requester;
        string ipfsHash;
        string mediaType;
        uint256 timestamp;
        bool isProcessed;
        bool isAuthentic;
        uint256 confidenceScore; // 0-100
        string aiModel;
        uint256 nftTokenId; // 0 if no NFT minted
    }

    // Struct to store verification result
    struct VerificationResult {
        bytes32 requestId;
        bool isAuthentic;
        uint256 confidenceScore;
        string aiModel;
        string reasoning;
        uint256 timestamp;
    }

    // Contract state variables
    AuthenticityNFT public immutable authenticityNFT;
    uint64 public subscriptionId;
    uint32 public gasLimit;
    bytes32 public donID;
    string public sourceCode;

    // Mappings
    mapping(bytes32 => VerificationRequest) public verificationRequests;
    mapping(string => VerificationResult) public verificationResults; // ipfsHash => result
    mapping(address => bytes32[]) public userRequests;
    mapping(string => bool) public flaggedContent; // ipfsHash => flagged

    // Arrays for public access
    bytes32[] public allRequestIds;
    string[] public allVerifiedContent;
    string[] public allFlaggedContent;

    // Statistics
    uint256 public totalVerifications;
    uint256 public totalAuthentic;
    uint256 public totalFake;
    uint256 public totalNFTsMinted;

    // Fee structure
    uint256 public verificationFee = 0.001 ether; // Fee in native token
    uint256 public nftMintFee = 0.01 ether; // Additional fee for NFT minting

    // Events
    event VerificationRequested(
        bytes32 indexed requestId,
        address indexed requester,
        string ipfsHash,
        string mediaType,
        uint256 timestamp
    );

    event VerificationCompleted(
        bytes32 indexed requestId,
        string indexed ipfsHash,
        bool isAuthentic,
        uint256 confidenceScore,
        string aiModel,
        uint256 timestamp
    );

    event ContentFlagged(
        string indexed ipfsHash,
        address indexed reporter,
        uint256 timestamp
    );

    event NFTMinted(
        string indexed ipfsHash,
        address indexed owner,
        uint256 tokenId,
        uint256 timestamp
    );

    event FeesUpdated(
        uint256 verificationFee,
        uint256 nftMintFee,
        uint256 timestamp
    );

    event FundsWithdrawn(
        address indexed owner,
        uint256 amount,
        uint256 timestamp
    );

    /**
     * @dev Constructor to initialize the contract
     * @param _router Chainlink Functions router address
     * @param _subscriptionId Chainlink subscription ID
     * @param _gasLimit Gas limit for Functions requests
     * @param _donID Decentralized Oracle Network ID
     * @param _sourceCode JavaScript source code for Chainlink Functions
     * @param _nftContract Address of the AuthenticityNFT contract
     * @param _initialOwner Initial owner of the contract
     */
    constructor(
        address _router,
        uint64 _subscriptionId,
        uint32 _gasLimit,
        bytes32 _donID,
        string memory _sourceCode,
        address _nftContract,
        address _initialOwner
    ) FunctionsClient(_router) Ownable(_initialOwner) {
        subscriptionId = _subscriptionId;
        gasLimit = _gasLimit;
        donID = _donID;
        sourceCode = _sourceCode;
        authenticityNFT = AuthenticityNFT(_nftContract);
    }

    /**
     * @notice Request verification for media content
     * @param _ipfsHash IPFS hash of the media to verify
     * @param _mediaType Type of media (image, video, audio)
     * @return requestId The ID of the verification request
     */
    function requestVerification(
        string memory _ipfsHash,
        string memory _mediaType
    ) external payable nonReentrant whenNotPaused returns (bytes32) {
        require(msg.value >= verificationFee, "Insufficient fee");
        require(bytes(_ipfsHash).length > 0, "Invalid IPFS hash");
        require(bytes(_mediaType).length > 0, "Invalid media type");
        require(
            verificationResults[_ipfsHash].timestamp == 0,
            "Content already verified"
        );

        // Create Chainlink Functions request
        FunctionsRequest.Request memory req;
        req.initializeRequestForInlineJavaScript(sourceCode);

        // Set arguments: [ipfsHash, mediaType]
        string[] memory args = new string[](2);
        args[0] = _ipfsHash;
        args[1] = _mediaType;
        req.setArgs(args);

        // Send request
        bytes32 requestId = _sendRequest(
            req.encodeCBOR(),
            subscriptionId,
            gasLimit,
            donID
        );

        // Store request data
        verificationRequests[requestId] = VerificationRequest({
            requester: msg.sender,
            ipfsHash: _ipfsHash,
            mediaType: _mediaType,
            timestamp: block.timestamp,
            isProcessed: false,
            isAuthentic: false,
            confidenceScore: 0,
            aiModel: "",
            nftTokenId: 0
        });

        // Update mappings and arrays
        userRequests[msg.sender].push(requestId);
        allRequestIds.push(requestId);
        totalVerifications++;

        emit VerificationRequested(
            requestId,
            msg.sender,
            _ipfsHash,
            _mediaType,
            block.timestamp
        );

        return requestId;
    }

    /**
     * @notice Chainlink Functions callback - processes verification result
     * @param requestId The ID of the request
     * @param response The verification result from AI service
     * @param err Any error that occurred during processing
     */
    function fulfillRequest(
        bytes32 requestId,
        bytes memory response,
        bytes memory err
    ) internal override {
        VerificationRequest storage request = verificationRequests[requestId];
        require(request.timestamp > 0, "Request not found");
        require(!request.isProcessed, "Request already processed");

        if (err.length > 0) {
            // Handle error case
            request.isProcessed = true;
            return;
        }

        // Decode response: [isAuthentic, confidenceScore, aiModel, reasoning]
        (bool isAuthentic, uint256 confidenceScore, string memory aiModel, string memory reasoning) = 
            abi.decode(response, (bool, uint256, string, string));

        // Update request
        request.isProcessed = true;
        request.isAuthentic = isAuthentic;
        request.confidenceScore = confidenceScore;
        request.aiModel = aiModel;

        // Store result
        verificationResults[request.ipfsHash] = VerificationResult({
            requestId: requestId,
            isAuthentic: isAuthentic,
            confidenceScore: confidenceScore,
            aiModel: aiModel,
            reasoning: reasoning,
            timestamp: block.timestamp
        });

        // Update statistics
        if (isAuthentic) {
            totalAuthentic++;
            allVerifiedContent.push(request.ipfsHash);
        } else {
            totalFake++;
            flaggedContent[request.ipfsHash] = true;
            allFlaggedContent.push(request.ipfsHash);
        }

        emit VerificationCompleted(
            requestId,
            request.ipfsHash,
            isAuthentic,
            confidenceScore,
            aiModel,
            block.timestamp
        );
    }

    /**
     * @notice Mint NFT certificate for authentic content
     * @param _ipfsHash IPFS hash of the verified authentic content
     * @return tokenId The ID of the minted NFT
     */
    function mintAuthenticityNFT(
        string memory _ipfsHash
    ) external payable nonReentrant whenNotPaused returns (uint256) {
        require(msg.value >= nftMintFee, "Insufficient NFT mint fee");

        VerificationResult memory result = verificationResults[_ipfsHash];
        require(result.timestamp > 0, "Content not verified");
        require(result.isAuthentic, "Content is not authentic");
        require(result.confidenceScore >= 80, "Confidence score too low");

        // Find the original request to check ownership
        bytes32 requestId = result.requestId;
        VerificationRequest storage request = verificationRequests[requestId];
        require(request.requester == msg.sender, "Only requester can mint NFT");
        require(request.nftTokenId == 0, "NFT already minted");

        // Mint NFT
        uint256 tokenId = authenticityNFT.mintAuthenticityNFT(
            msg.sender,
            _ipfsHash,
            result.confidenceScore,
            result.aiModel
        );

        // Update request
        request.nftTokenId = tokenId;
        totalNFTsMinted++;

        emit NFTMinted(_ipfsHash, msg.sender, tokenId, block.timestamp);

        return tokenId;
    }

    /**
     * @notice Get verification result for content
     * @param _ipfsHash IPFS hash of the content
     * @return result The verification result
     */
    function getVerificationResult(
        string memory _ipfsHash
    ) external view returns (VerificationResult memory) {
        return verificationResults[_ipfsHash];
    }

    /**
     * @notice Get user's verification requests
     * @param _user Address of the user
     * @return requestIds Array of request IDs
     */
    function getUserRequests(
        address _user
    ) external view returns (bytes32[] memory) {
        return userRequests[_user];
    }

    /**
     * @notice Get all verified authentic content
     * @return Array of IPFS hashes
     */
    function getAllVerifiedContent() external view returns (string[] memory) {
        return allVerifiedContent;
    }

    /**
     * @notice Get all flagged content
     * @return Array of IPFS hashes
     */
    function getAllFlaggedContent() external view returns (string[] memory) {
        return allFlaggedContent;
    }

    /**
     * @notice Get platform statistics
     * @return totalVerifications_ Total number of verifications
     * @return totalAuthentic_ Total authentic content
     * @return totalFake_ Total fake content
     * @return totalNFTsMinted_ Total NFTs minted
     */
    function getStatistics() external view returns (
        uint256 totalVerifications_,
        uint256 totalAuthentic_,
        uint256 totalFake_,
        uint256 totalNFTsMinted_
    ) {
        return (totalVerifications, totalAuthentic, totalFake, totalNFTsMinted);
    }

    /**
     * @notice Update verification fees (owner only)
     * @param _verificationFee New verification fee
     * @param _nftMintFee New NFT mint fee
     */
    function updateFees(
        uint256 _verificationFee,
        uint256 _nftMintFee
    ) external onlyOwner {
        verificationFee = _verificationFee;
        nftMintFee = _nftMintFee;

        emit FeesUpdated(_verificationFee, _nftMintFee, block.timestamp);
    }

    /**
     * @notice Update Chainlink Functions configuration (owner only)
     * @param _subscriptionId New subscription ID
     * @param _gasLimit New gas limit
     * @param _donID New DON ID
     * @param _sourceCode New source code
     */
    function updateChainlinkConfig(
        uint64 _subscriptionId,
        uint32 _gasLimit,
        bytes32 _donID,
        string memory _sourceCode
    ) external onlyOwner {
        subscriptionId = _subscriptionId;
        gasLimit = _gasLimit;
        donID = _donID;
        sourceCode = _sourceCode;
    }

    /**
     * @notice Withdraw contract funds (owner only)
     */
    function withdrawFunds() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds to withdraw");

        (bool success, ) = payable(owner()).call{value: balance}("");
        require(success, "Withdrawal failed");

        emit FundsWithdrawn(owner(), balance, block.timestamp);
    }

    /**
     * @notice Pause contract (owner only)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause contract (owner only)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Emergency function to flag content manually (owner only)
     * @param _ipfsHash IPFS hash of content to flag
     */
    function emergencyFlagContent(string memory _ipfsHash) external onlyOwner {
        require(!flaggedContent[_ipfsHash], "Content already flagged");

        flaggedContent[_ipfsHash] = true;
        allFlaggedContent.push(_ipfsHash);

        emit ContentFlagged(_ipfsHash, msg.sender, block.timestamp);
    }

    /**
     * @notice Get contract version
     * @return Version string
     */
    function version() external pure returns (string memory) {
        return "1.0.0";
    }
}

contracts/SimpleAuthenticityNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SimpleAuthenticityNFT
 * @dev A simplified NFT contract for testing compilation
 */
contract SimpleAuthenticityNFT is ERC721, Ownable {
    uint256 private _nextTokenId;

    constructor(address initialOwner) 
        ERC721("Authenticity Certificate", "AUTH") 
        Ownable(initialOwner)
    {
        _nextTokenId = 1;
    }

    function mint(address to) external onlyOwner returns (uint256) {
        uint256 tokenId = _nextTokenId++;
        _mint(to, tokenId);
        return tokenId;
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");
        return "https://api.deepfake-checker.com/metadata/";
    }
}

